<?php

namespace Billetterie\BilletterieBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * TarifRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TarifRepository extends EntityRepository
{
    public function RecupTarifBillet ($id)
    {
        $qb = $this->createQueryBuilder('a')
            ->leftJoin('Billetterie\BilletterieBundle\Entity\Client', 'c')
            ->addSelect('a')
            ->where('a.id = c.tarif')
            ->andWhere('c.panier = :id')
            ->setParameter('id', $id);

        return $qb  ->getQuery()
                    ->getResult();
    }

}
